from django.urls import path, re_path

from rest_auth.views import (
    LoginView, LogoutView, UserDetailsView, PasswordChangeView,
    PasswordResetView, PasswordResetConfirmView
)

urlpatterns = [
    # URLs that do not require a session or valid token
    re_path('password/reset/$', PasswordResetView.as_view(),
            name='rest_password_reset'),
    re_path('password/reset/confirm/$', PasswordResetConfirmView.as_view(),
            name='rest_password_reset_confirm'),
    re_path('login/$', LoginView.as_view(), name='rest_login'),
    # URLs that require a user to be logged in with a valid session / token.
    re_path('logout/$', LogoutView.as_view(), name='rest_logout'),
    re_path('user/$', UserDetailsView.as_view(), name='rest_user_details'),
    re_path('password/change/$', PasswordChangeView.as_view(),
            name='rest_password_change'),
]
